import React, { useState, useEffect } from 'react';

const Note = () => {
  const [notes, setNotes] = useState([]);
  const [inputE, setInputE] = useState('');
  const [inputG, setInputG] = useState('');
  const [index, getIndex] = useState('');
  const [flippedCards, setFlippedCards] = useState([]);

  useEffect(() => {
    fetchNotes();
  }, []);

  const fetchNotes = async () => {
    try {
      const response = await fetch('/api/notes');
      const data = await response.json();
      setNotes(data);
      setFlippedCards(new Array(data.length).fill(false));
    } catch (error) {
      console.error('Error fetching notes:', error);
    }
  };

  const addNote = async () => {
    if (inputE.trim() !== '' && inputG.trim() !== '') {
      try {
        const response = await fetch('/api/notes', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ german: inputG, english: inputE }),
        });
        const newNote = await response.json();
        setNotes([...notes, newNote]);
        setFlippedCards([...flippedCards, false]);
        setInputG('');
        setInputE('');
      } catch (error) {
        console.error('Error adding note:', error);
      }
    }
  };

  const deleteNote = async (id) => {
    try {
      await fetch(`/api/notes/${id}`, {
        method: 'DELETE',
      });
      const newNotes = notes.filter(note => note.id !== id);
      setNotes(newNotes);
      setFlippedCards(flippedCards.filter((_, i) => i !== id));
    } catch (error) {
      console.error('Error deleting note:', error);
    }
  };

  const getNote = () => {
    const gotNote = notes[index];
    alert(gotNote ? `${gotNote[1]} - ${gotNote[0]}` : 'Note not found');
  };

  const flipCard = (idx) => {
    const newFlippedCards = [...flippedCards];
    newFlippedCards[idx] = !newFlippedCards[idx];
    setFlippedCards(newFlippedCards);
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>My Notes</h2>
      <div>
        <input
          type="text"
          value={inputG}
          onChange={(e) => setInputG(e.target.value)}
          placeholder="Enter a German note..."
          style={{ padding: '10px', width: '45%', marginRight: '10px' }}
        />
        <input
          type="text"
          value={inputE}
          onChange={(e) => setInputE(e.target.value)}
          placeholder="Enter an English note..."
          style={{ padding: '10px', width: '45%' }}
        />
        <button onClick={addNote} style={{ padding: '10px 20px', marginLeft: '10px' }}>
          Add Note
        </button>
      </div>
      <ul style={{ marginTop: '20px', listStyleType: 'none', padding: 0 }}>
        {notes.map((note, idx) => {
          const [firstValue, secondValue] = note;
          return (
            <li
              key={idx}
              style={{
                marginBottom: '20px',
                padding: '10px',
                backgroundColor: '#f9f9f9',
                border: '1px solid #ddd',
                borderRadius: '5px',
                display: 'flex',
                flexDirection: 'column'
              }}
            >
              <div
                style={{
                  display: 'flex',
                  justifyContent: 'space-between',
                  alignItems: 'center',
                  width: '100%',
                  marginBottom: '10px',
                }}
              >
                <div>{flippedCards[idx] ? secondValue : firstValue}</div>
                <button onClick={() => flipCard(idx)} style={{ marginLeft: '10px' }}>
                  Flip
                </button>
              </div>
              <button
                onClick={() => deleteNote(idx)}
                style={{
                  width: '20%',
                  padding: '5px',
                  marginTop: '10px',
                }}
              >
                Delete
              </button>
            </li>
          );
        })}
      </ul>
      <div>
        <input
          type="text"
          value={index}
          onChange={(e) => getIndex(e.target.value)}
          placeholder="Look up a note by Index..."
          style={{ padding: '10px', marginTop:'3em', width: '70%'}}
        />
        <button onClick={getNote} style={{ padding: '10px 20px', marginLeft: '10px' }}>
          Search by Index
        </button>
      </div>
    </div>
  );
};

export default Note;
