import React, { useState, useEffect } from 'react';

const Note = () => {
  const [notes, setNotes] = useState([]);
  const [inputE, setInputE] = useState('');
  const [inputG, setInputG] = useState('');
  const [index, getIndex] = useState('');
  const [flippedCards, setFlippedCards] = useState([]);

  useEffect(() => {
    fetchNotes();
  }, []);

  const fetchNotes = async () => {
    try {
      const response = await fetch('/api/notes');
      const data = await response.json();
      setNotes(data);
      setFlippedCards(new Array(data.length).fill(false));
    } catch (error) {
      console.error('Error fetching notes:', error);
    }
  };

  const addNote = async () => {
    if (inputE.trim() !== '' && inputG.trim() !== '') {
      try {
        const response = await fetch('/api/notes', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ german: inputG, english: inputE }),
        });
        const newNote = await response.json();
        setNotes([...notes, newNote]);
        setFlippedCards([...flippedCards, false]);
        setInputG('');
        setInputE('');
      } catch (error) {
        console.error('Error adding note:', error);
      }
    }
  };

  const deleteNote = async (id) => {
    try {
      await fetch(`/api/notes/${id}`, {
        method: 'DELETE',
      });
      const newNotes = notes.filter(note => note.id !== id);
      setNotes(newNotes);
      setFlippedCards(flippedCards.filter((_, i) => i !== id));
    } catch (error) {
      console.error('Error deleting note:', error);
    }
  };

  // ... rest of your component code (getNote, flipCard, and render) ...

};

export default Note;
